mapper.insert:
INSERT INTO customer(customer_id, customer_sn, name, business, region, type, phone, level, importance, status, address, owner, remark, contacts_json) VALUES (#{customer.customerId}, #{customer.customerSn}, #{customer.name}, #{customer.business}, #{customer.region}, #{customer.type}, #{customer.phone}, #{customer.level}, #{customer.importance}, #{customer.status}, #{customer.address}, #{customer.owner}, #{customer.remark}, #{customer.contactsJson})
ON DUPLICATE KEY UPDATE name = #{customer.name}, business = #{customer.business}, region = #{customer.region}, type = #{customer.type}, phone = #{customer.phone}, level = #{customer.level}, importance = #{customer.importance}, status = #{customer.status}, address = #{customer.address}, owner = #{customer.owner}, remark = #{customer.remark}
, contacts_json = #{customer.contactsJson}

mapper.select:
SELECT EXISTS (
    SELECT 1 FROM customer WHERE

        customer_sn=#{customerSn}


        customer_sn=#{customerSn} AND customer_id != #{customerId}

)
