mapper.insert:
INSERT INTO paper_directory(paper_directory_id, name, parent_paper_directory_id, tenant_code)
VALUES(#{paperDirectoryId}, #{name}, #{parentPaperDirectoryId}, #{tenantCode})
ON DUPLICATE KEY UPDATE name=#{name}, parent_paper_directory_id=#{parentPaperDirectoryId}, tenant_code=#{tenantCode}

INSERT INTO knowledge_point(knowledge_point_id, name, parent_knowledge_point_id, type, tenant_code)
VALUES(#{knowledgePointId}, #{name}, #{parentKnowledgePointId}, #{type}, #{tenantCode})
ON DUPLICATE KEY UPDATE name=#{name}, parent_knowledge_point_id=#{parentKnowledgePointId}, tenant_code=#{tenantCode}

INSERT INTO paper_knowledge_point_relation(paper_id, knowledge_point_id)
SELECT #{paperId}, #{knowledgePointId} FROM dual WHERE NOT EXISTS(
    SELECT 1 FROM paper_knowledge_point_relation
    WHERE paper_id=#{paperId} AND knowledge_point_id=#{knowledgePointId}
)
